import React from 'react'
import { useState, useRef } from 'react'


const ScrollDrag = ({ children }) => {
    const [isScrolling, setIsScrolling] = useState(false);
    const [clientX, setClientX] = useState(0);
    const [scrollX, setScrollX] = useState(0);
    const [clientY, setClientY] = useState(0);
    const [scrollY,setScrollY] = useState[0]; 

    const scrollRef = useRef({}); //TODO: SET INITIAL VALUE??

    const onMouseDown = e =>{
        setIsScrolling(true);
        setClientX(e.clientX)
    };

    const onMouseUp = () =>{
        setIsScrolling(false);
    };

    const onMouseMove = e =>{
        if(!isScrolling) return;
        scrollRef.scrollLeft = scrollX + e.clientX - clientX;
        setScrollX(scrollX + e.clientX - clientX);
        setClientX(e.clientX);

        scrollRef.scrollTop = scrollY + e.clientY - clientY;
        setScrollY(scrollY + e.clientY - clientX);
        setClientY(e.clientY);
    }

 return (
    <div
    ref={scrollRef}
    onMouseDown={onMouseDown}
    onMouseUp={onMouseUp}
    onMouseMove={onMouseMove}
    classname="drag-to-scroll"
    >
        {children}
    </div>
  )
}

export default ScrollDrag
