import React, { useEffect } from 'react'
import { useState } from 'react';
import Popup from './Popup';
//    {/*<div onDoubleClick={() => console.log("clicked")}>{text}</div>*/}

const Square = ({text, socket,index}) => {
  const [active, setActive] = useState(false);
  const [imgSrc, setImageSrc] = useState("")

  const onDoubleClick = () =>{
    setActive(true);
  }

  useEffect(()=>{
    setImageSrc(!text ? "https://www.google.com/url?sa=i&url=https%3A%2F%2Fwww.i2symbol.com%2Fsymbols%2Fpunctuation%2Fx02E3-modifier-letter-small-x&psig=AOvVaw0-0gfvwXo0ghKtlcy31Gcs&ust=1663869746095000&source=images&cd=vfe&ved=0CAwQjRxqFwoTCNjjwvu7pvoCFQAAAAAdAAAAABAD"
    : "https://pbs.twimg.com/profile_images/1455185376876826625/s1AjSxph_400x400.jpg")
  }, [text])

  return (
    <>
      <div onDoubleClick={onDoubleClick}>
        <img src={imgSrc}/>
      </div>

      {active 
      ?
      <>
        <Popup imgSrc={imgSrc} socket={socket} index={index}/>
        <div id='overlay' onDoubleClick={() => setActive(false)}/>
      </>
      : <></>
      }  

    </>
  )
}

export default Square
