import React from 'react'
import { useState } from 'react';
import Popup from './Popup';
//    {/*<div onDoubleClick={() => console.log("clicked")}>{text}</div>*/}

const Square = ({text}) => {
  const [active, setActive] = useState(false);

  const onDoubleClick = () =>{
    setActive(true);
  }

  return (
    <>
      <div onDoubleClick={onDoubleClick}>
        {!text
        ?<img src='https://i.pcmag.com/imagery/reviews/03aizylUVApdyLAIku1AvRV-39.fit_scale.size_760x427.v1605559903.png'></img>
        :<img src='https://pbs.twimg.com/profile_images/1455185376876826625/s1AjSxph_400x400.jpg'></img>
        }
      </div>

      {active 
      ?
      <>
        <Popup props={setActive}/>
        <div id='overlay' onClick={() => setActive(false)}/>
      </>
      : <></>
      }  

    </>
  )
}

export default Square
