import React from 'react'
import {useRef, useState, useEffect} from 'react';
import axios from '../api/axios';
import useAuth from '../hooks/useAuth';

//so will send to correct page once signed in:
import { Link, useNavigate, useLocation } from 'react-router-dom';

const LOGIN_URL = '/api/auth/login'

const Login = () => {
    const {setAuth, persist, setPersist} = useAuth(); //allows to change global 'auth' context

    const navigate = useNavigate();
    const location = useLocation();
    const from = location.state?.from?.pathname || "/"; //so can send user back to where they came from (or otherwise homepage)

    const userRef = useRef();
    const errRef = useRef();

    const [user,setUser] = useState('');
    const [pwd,setPwd] = useState("");    
    const [errMsg, setErrMsg] = useState('');
    //const [success, setSuccess] = useState(false);

    //set focus to username input when page loaded
    useEffect(()=>{
        userRef.current.focus()
    },[]);

    useEffect(() =>{
        setErrMsg('');
    }, [user,pwd]);

    const submit = async(e) =>{
        e.preventDefault();

        if(!(user&&pwd)){
            setErrMsg("Invalid Entry")
            return;
        }

        //send login post request:
        try{
            const response = await axios.post(LOGIN_URL, JSON.stringify({username: user,password: pwd}),
            {
                headers: {'Content-Type': 'application/json'},
                withCredentials: true,
            });
            console.log(JSON.stringify(response?.data));
            const accessToken = response?.data?.accessToken;
            const role = response?.data?.role;
            setAuth({user,pwd,role,accessToken});
            setUser('');
            setPwd('');
            //setSuccess(true);
            navigate(from, {replace:true}) //(replace:true so that if user presses back arrow again wont be sent back to sign in page)
            return;
        }catch(err){
            if(!err?.response){
                setErrMsg('No server response');
            }else if(err.response?.status===0){
                setErrMsg("Error connecting to server")
            }else{
                setErrMsg("Incorrect Login")
            }
            errRef.current.focus();
        }
    }

    const togglePersist =()=>{
        setPersist(prev => !prev);
    }

    useEffect(() =>{ //whenever value of persist changes, we want to store it in local storage (JUST BOOLEAN VAL - NOT IMPORTANT DATA / TOKEN ETC)
        localStorage.setItem("persist", persist);
    }, [persist])

    return (
        <section>
            <p ref={errRef} className={errMsg? "errmsg":"offscreen"} aria-live="assertive">{errMsg}</p>
            <h1>Login</h1>
            <form onSubmit={submit}>
                    <label htmlFor='username'>
                        Username:
                    </label>
                    <input 
                    type="text"
                    id="username"
                    ref={userRef}
                    required
                    autoComplete="on"
                    onChange={(e)=> setUser(e.target.value)}
                    value={user}
                    />
                    <label htmlFor='password'>
                        Password:
                    </label>
                    <input 
                    type="password"
                    id="password"
                    required
                    autoComplete="off"
                    onChange={(e)=> setPwd(e.target.value)}
                    value={pwd}
                    />
                    {/*(Button type is submit by default)*/}
                    <button disabled = {!(user && pwd)}>
                        Login
                    </button>
                    <div className="persistCheck">
                        <input 
                            type="checkbox"
                            id="persist"
                            onChange={togglePersist}
                            checked={persist} 
                        />
                        <label htmlFor="persist">Trust this device</label>
                        {/*^ For persist login */} 
                    </div>
                    <p>
                        Need an account?<br />
                        <span className="link">
                            <Link to="/register">Register</Link>
                        </span>
                    </p>
            </form>
        </section>
    )
}


export default Login
