import React, { useEffect } from 'react'
import Square from './Square'
import { useState, useRef } from 'react';
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome"
import { faMagnifyingGlassPlus, faMagnifyingGlassMinus } from '@fortawesome/free-solid-svg-icons';

import "./css/Squares.css"

const HEIGHT_IN_SQUARES = 11;
const MAX_PIXEL_WIDTH = 500; //of each square
const MIN_PIXEL_WIDTH = 200;

const SquaresPage = () => {

  const scrollRef = useRef(); //reference to entire window of squares

  const [isDown, setIsDown] = useState(false);
  const [startX, setStartX] = useState(0);
  const [startY, setStartY] = useState(0);
  const [scrollLeft, setScrollLeft] = useState(0);
  const [scrollTop, setScrollTop] = useState(0); 


  /*
  useEffect(() =>{
    console.log({isDown, startX, startY,scrollLeft,scrollTop});
  },[isDown, startX, startY,scrollLeft,scrollTop]) 
*/

  const onMouseDown = e =>{
      setIsDown(true);
      const slider = scrollRef.current
      slider.classList.add("active");

      setStartX(e.pageX - slider.offsetLeft);
      setStartY(e.pageY - slider.offsetTop);

      setScrollLeft(slider.scrollLeft);
      setScrollTop(slider.scrollTop);

  };

  const onMouseUp = () =>{
    setIsDown(false);
    scrollRef.current.classList.remove("active");

  };

  const onMouseMove = e =>{
      e.preventDefault();
      if(!isDown) return;
      const slider = scrollRef.current
      const x = e.pageX - slider.offsetLeft;
      const y = e.pageY - slider.offsetTop;

      const walkx = x-startX;
      const walky = y-startY;

      slider.scrollLeft = scrollLeft-walkx;
      slider.scrollTop = scrollTop - walky;
  }

  const onMouseLeave= () => {
    setIsDown(false);
    scrollRef.current.classList.remove("active");
  }
  //TODO: Add on mouse leave, setIsDown(false)

  return (
    <>
      <button className="zoom" id="zoom-plus" onClick={()=>console.log("plus")}>
        <FontAwesomeIcon icon={faMagnifyingGlassPlus} />
      </button>

      <button className="zoom" id="zoom-minus" onClick={()=>console.log("minus")}>
        <FontAwesomeIcon icon={faMagnifyingGlassMinus} />
      </button>


    </>
  )
}

export default SquaresPage
