import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCoins } from '@fortawesome/free-solid-svg-icons';
import useAuth from '../hooks/useAuth'
import { useEffect, useState } from 'react';
import useAxiosPrivate from '../hooks/useAxiosPrivate';

import "./css/Account.css" 
import axios from '../api/axios';

const Account = () => {
    const {auth} = useAuth();
    const axiosPrivate = useAxiosPrivate();
    const [image, setImage] = useState({});
    const [imageUrl, setImageUrl] = useState();


    const upload = async(e)=>{
        e.preventDefault();
        //TODO: then change the fetch() here to axios statment, plus add so new url stored in mongodb

        const response = await axiosPrivate.get('/api/s3/geturl');
        const url = response.data.url;

        /*
        await axios.put(url, {
            headers:{
                "Content-Type":"multipart/form-data"
            }, 
            body: image
        })

        */
       console.log(image);

    }

    const onImageChange = (e) =>{
        setImage(e.target.files[0]);
    }

    useEffect(() =>{
        if(!image.name) return;
        setImageUrl(URL.createObjectURL(image));
    },[image])

    return (        
        <div id="profile">
            <div id="user-data">
                <h1>{auth.user}</h1>
                <br />
                <p><FontAwesomeIcon icon={faCoins}/>{auth.coins}</p>
            </div>
            <form onSubmit={upload}>
                <input id='imageInput' type="file" accept="image/*" onChange={onImageChange}/>
                <img src={imageUrl}/>
                <button type="submit">Upload</button>
            </form>
        </div>
    )
}

export default Account
