import React from 'react'
import { Link } from 'react-router-dom';
import useAuth from '../hooks/useAuth';
import useRefreshToken from '../hooks/useRefreshToken';

const Popup = ({imgSrc,socket,index}) => {

    const {auth} = useAuth();
    const refresh = useRefreshToken();

    //called if error with authentication token
    const retryWithNewAT = async() =>{
        try{
            refresh();
        } catch (err){
            console.log("Error");
        }
    }

    const buy = ()=>{
        console.log(`buy ${index}`)
        socket.emit("buy",index,auth.accessToken);
    }

    return (
    <div className="Popup">
        <img src={imgSrc}/>

        {auth.user
        ? <button onClick={buy}>Buy</button>
        : 
        <div>
            <p>In order to buy square, you must be logged in</p>
            <Link to="login">
                <button>Login</button>
            </Link>
        </div>
    }
    </div>
  )
}

export default Popup
